    private void btnSignInMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnSignInMouseClicked
        String uname = txtUsername.getText();
        //setTitle("Hello "+uname);
        //String upass = txtPassword.getText();
        
        //String upass = txtPassword.getPassword().toString();
        
        String upass=new String(txtPassword.getPassword());
        
        System.out.println(uname+"    "+upass);
        
        if(uname.equals("admin") && upass.equals("12345"))
        {
            lblInfo.setText("Authenticated");
        }
        else
        {
            lblInfo.setText("Invalid Username/Password");
            txtUsername.setText("");
            txtPassword.setText("");
            txtUsername.requestFocus();
        }
        
    }//GEN-LAST:event_btnSignInMouseClicked

